code.installcode:
$db->query("CREATE TABLE " . TABLE_PREFIX . "giftimages (image_id INT NOT NULL AUTO_INCREMENT, image_url VARCHAR(512) NOT NULL, PRIMARY KEY (image_id))");
$db->query("CREATE TABLE " . TABLE_PREFIX . "giftstore (gift_id INT NOT NULL AUTO_INCREMENT, seller_id INT NOT NULL, gift_name VARCHAR(128) NOT NULL, gift_description VARCHAR(256), gift_price INT NOT NULL, image_id INT NOT NULL, PRIMARY KEY (gift_id), INDEX (seller_id), INDEX (image_id), FOREIGN KEY (image_id) REFERENCES giftimages (image_id))");
$db->query("CREATE TABLE " . TABLE_PREFIX . "giftpoints_give (seller_id INT NOT NULL, points_count INT NOT NULL, PRIMARY KEY (seller_id))");
$db->query("CREATE TABLE " . TABLE_PREFIX . "giftpoints_spend (seller_id INT NOT NULL, buyer_id INT NOT NULL, points_count INT NOT NULL, PRIMARY KEY (seller_id, buyer_id))");
$db->query("CREATE TABLE " . TABLE_PREFIX . "giftbuy (buy_id INT NOT NULL AUTO_INCREMENT, gift_id INT NOT NULL, buyer_id INT NOT NULL, status TINYINT(1) NOT NULL, seller_comment VARCHAR(256), buyer_comment VARCHAR(256), PRIMARY KEY (buy_id))");

code.uninstallcode:
$db->query("DROP TABLE " . TABLE_PREFIX . "giftstore");
$db->query("DROP TABLE " . TABLE_PREFIX . "giftpoints_give");
$db->query("DROP TABLE " . TABLE_PREFIX . "giftpoints_spend");
$db->query("DROP TABLE " . TABLE_PREFIX . "giftbuy");
$db->query("DROP TABLE " . TABLE_PREFIX . "giftimages");
